import React, {Component} from 'react'; /*nimi*/
import './Speedgame.css'; /*polun kanssa */

class Speedgame extends Component {
     state = {
        counter: 0 /* voi lis채t채 muitakin pilkulla erotettuna*/
    }
    

    startHandler = () => {
      this.setState ({
        counter: this.state.counter + 1
        })
    }

    stopHandler = () => {
      if (this.state.counter > 0) {
        this.setState ({
        counter: this.state.counter - 1
        })
      }
    }
  
    resetHandler = () => {
      this.setState ({
        counter: 0
      })
    }
    

  render() {
    return (<div className = 'container'>
        <div className = 'circle'></div>

        <div className = 'circle'></div>

        <div className = 'circle'></div>

        <div className = 'circle'></div>

        <div> 
        <button onClick = { this.startHandler } > START the GAME </button>        
        <button onClick = { this.stopHandler } > STOP the GAME </button>
        </div> 
    </div> 
    );
     
    
  
     
    
    
  
    

            

}
}

export default Speedgame;
