import React from 'react';
import './App.css';
import './index.css';
import Circle from './Circle.js';
import GameOver from './GameOver.js';
import Score from './Score.js';

startHandler = () => {
  this.setState ({
    counter: this.state.counter + 1
    })
}

stopHandler = () => {
  if (this.state.counter > 0) {
    this.setState ({
    counter: this.state.counter - 1
    })
  }
}

resetHandler = () => {
  this.setState ({
    counter: 0
  })
}

<div className = 'buttons'> 
            <button1 onClick = { this.startHandler } > START the GAME </button1>        
            <button2 onClick = { this.stopHandler } >  STOP the GAME  </button2>
        </div> 

const App = () => {
  return (
    <div className="App">
      <Circle /> 
      <GameOver /> 
      <Score />
    </div>

  ); 
}

export default App;
